import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Check if user is already logged in
    const token = localStorage.getItem('token');
    if (token) {
      // Set the default Authorization header for all requests
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // Get user info if token exists
      fetchCurrentUser();
    } else {
      setLoading(false);
    }
  }, []);

  const fetchCurrentUser = async () => {
    try {
      // This endpoint would need to be implemented in your backend
      // const response = await axios.get('/api/auth/me');
      // setCurrentUser(response.data);

      // For now, we'll just use the token to determine if user is logged in
      const token = localStorage.getItem('token');
      if (token) {
        // Parse the JWT token to get user info (this is just for demo purposes)
        // In a real app, you should validate the token on the server
        const base64Url = token.split('.')[1];
        const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
        const jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
          return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
        }).join(''));

        const payload = JSON.parse(jsonPayload);
        setCurrentUser({
          id: payload.sub,
          // Add other user properties as needed
        });
      }
    } catch (err) {
      console.error('Error fetching current user:', err);
      setError('Failed to fetch user information');
      // Clear token if it's invalid
      localStorage.removeItem('token');
      delete axios.defaults.headers.common['Authorization'];
    } finally {
      setLoading(false);
    }
  };

  const login = async (email, password) => {
    try {
      setLoading(true);
      setError(null);

      const response = await axios.post('/api/auth/login', { email, password });
      const { accessToken } = response.data;

      // Save token to localStorage
      localStorage.setItem('token', accessToken);

      // Set the default Authorization header for all requests
      axios.defaults.headers.common['Authorization'] = `Bearer ${accessToken}`;

      // Fetch current user info
      await fetchCurrentUser();

      return true;
    } catch (err) {
      console.error('Login error:', err);
      setError(err.response?.data?.error || 'Failed to login');
      return false;
    } finally {
      setLoading(false);
    }
  };

  const register = async (userData) => {
    try {
      setLoading(true);
      setError(null);

      await axios.post('/api/auth/signup', userData);

      return true;
    } catch (err) {
      console.error('Registration error:', err);
      setError(err.response?.data?.error || 'Failed to register');
      return false;
    } finally {
      setLoading(false);
    }
  };

  const logout = () => {
    // Remove token from localStorage
    localStorage.removeItem('token');

    // Remove Authorization header
    delete axios.defaults.headers.common['Authorization'];

    // Clear user state
    setCurrentUser(null);
  };

  const value = {
    currentUser,
    loading,
    error,
    login,
    register,
    logout
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
