import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Grid,
  Paper,
  Box,
  Button,
  CircularProgress,
  Alert
} from '@mui/material';
import {
  Book as BookIcon,
  People as PeopleIcon,
  ImportContacts as BorrowingIcon,
  Warning as WarningIcon
} from '@mui/icons-material';
import { booksAPI, membersAPI, borrowingsAPI } from '../services/api';

const StatCard = ({ title, value, icon, color, onClick }) => {
  return (
    <Paper
      elevation={2}
      sx={{
        p: 3,
        display: 'flex',
        flexDirection: 'column',
        height: '100%',
        borderRadius: 2,
        cursor: onClick ? 'pointer' : 'default',
        '&:hover': onClick ? { boxShadow: 6 } : {},
        transition: 'box-shadow 0.3s ease-in-out'
      }}
      onClick={onClick}
    >
      <Box display="flex" alignItems="center" mb={2}>
        <Box
          sx={{
            backgroundColor: `${color}.light`,
            borderRadius: '50%',
            p: 1,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            mr: 2
          }}
        >
          {icon}
        </Box>
        <Typography variant="h6" color="text.secondary">
          {title}
        </Typography>
      </Box>
      <Typography variant="h3" component="div" fontWeight="bold">
        {value}
      </Typography>
    </Paper>
  );
};

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalBooks: 0,
    totalMembers: 0,
    totalBorrowings: 0,
    activeBorrowings: 0,
    overdueBorrowings: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStats = async () => {
      try {
        setLoading(true);
        setError(null);
        console.log('Fetching dashboard statistics...');

        // In a real application, you would have API endpoints for these statistics
        // For now, we'll simulate by counting the items

        const [booksResponse, membersResponse, borrowingsResponse] = await Promise.all([
          booksAPI.getAll(),
          membersAPI.getAll(),
          borrowingsAPI.getAll()
        ]);

        const books = booksResponse.data || [];
        const members = membersResponse.data || [];
        const borrowings = borrowingsResponse.data || [];

        console.log('Dashboard stats - Books:', books);
        console.log('Dashboard stats - Members:', members);
        console.log('Dashboard stats - Borrowings:', borrowings);

        const activeBorrowings = borrowings.filter(b => !b.returned);
        const today = new Date();
        const overdueBorrowings = activeBorrowings.filter(b => 
          new Date(b.dueDate) < today
        );

        const newStats = {
          totalBooks: books.length,
          totalMembers: members.length,
          totalBorrowings: borrowings.length,
          activeBorrowings: activeBorrowings.length,
          overdueBorrowings: overdueBorrowings.length
        };

        console.log('Setting dashboard stats:', newStats);
        setStats(newStats);
      } catch (err) {
        console.error('Error fetching dashboard stats:', err);
        setError('Failed to load dashboard statistics. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchStats();

    // Set up an interval to refresh stats every 30 seconds
    const intervalId = setInterval(fetchStats, 30000);

    // Clean up the interval when the component unmounts
    return () => clearInterval(intervalId);
  }, []);

  if (loading) {
    return (
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <Box display="flex" justifyContent="center" my={4}>
          <CircularProgress />
        </Box>
      </Container>
    );
  }

  if (error) {
    return (
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <Alert severity="error" sx={{ mb: 4 }}>
          {error}
        </Alert>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" gutterBottom>
        Dashboard
      </Typography>

      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Total Books"
            value={stats.totalBooks}
            icon={<BookIcon sx={{ color: 'primary.main' }} />}
            color="primary"
            onClick={() => navigate('/books')}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Total Members"
            value={stats.totalMembers}
            icon={<PeopleIcon sx={{ color: 'secondary.main' }} />}
            color="secondary"
            onClick={() => navigate('/members')}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Total Borrowings"
            value={stats.totalBorrowings}
            icon={<BorrowingIcon sx={{ color: 'info.main' }} />}
            color="info"
            onClick={() => navigate('/borrowings')}
          />
        </Grid>
      </Grid>

      <Typography variant="h5" gutterBottom>
        Borrowing Status
      </Typography>

      <Grid container spacing={3}>
        <Grid item xs={12} sm={6}>
          <StatCard
            title="Active Borrowings"
            value={stats.activeBorrowings}
            icon={<BorrowingIcon sx={{ color: 'success.main' }} />}
            color="success"
            onClick={() => navigate('/borrowings')}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <StatCard
            title="Overdue Borrowings"
            value={stats.overdueBorrowings}
            icon={<WarningIcon sx={{ color: 'error.main' }} />}
            color="error"
            onClick={() => navigate('/borrowings')}
          />
        </Grid>
      </Grid>

      {/* Debug section */}
      <Box sx={{ mt: 4, pt: 2, borderTop: '1px solid #eee' }}>
        <Typography variant="h6" gutterBottom>
          Debug Tools
        </Typography>
        <Button 
          variant="outlined" 
          color="secondary" 
          size="small"
          onClick={() => {
            // Reset mock data in localStorage
            localStorage.removeItem('mock_books');
            localStorage.removeItem('mock_members');
            localStorage.removeItem('mock_borrowings');
            // Reload the page to apply changes
            window.location.reload();
          }}
          sx={{ mr: 2 }}
        >
          Reset Mock Data
        </Button>
        <Button 
          variant="outlined" 
          color="primary" 
          size="small"
          onClick={() => {
            // Log current mock data to console
            console.log('Mock Books:', JSON.parse(localStorage.getItem('mock_books') || '[]'));
            console.log('Mock Members:', JSON.parse(localStorage.getItem('mock_members') || '[]'));
            console.log('Mock Borrowings:', JSON.parse(localStorage.getItem('mock_borrowings') || '[]'));
            // Show a message to check console
            alert('Mock data logged to console. Press F12 to view.');
          }}
        >
          View Mock Data
        </Button>
      </Box>
    </Container>
  );
};

export default Dashboard;
