import axios from 'axios';

// Create axios instance with base URL
const api = axios.create({
  baseURL: '/api',
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add a request interceptor to add the auth token to requests
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers['Authorization'] = `Bearer ${token}`;
    }
    console.log('API Request:', config.method.toUpperCase(), config.url, config.data);
    return config;
  },
  (error) => {
    console.error('API Request Error:', error);
    return Promise.reject(error);
  }
);

// Add a response interceptor to handle errors
api.interceptors.response.use(
  (response) => {
    console.log('API Response:', response.status, response.data);
    return response;
  },
  (error) => {
    console.error('API Response Error:', error.response?.status, error.response?.data || error.message);

    // Handle 401 Unauthorized errors
    if (error.response && error.response.status === 401) {
      // Clear token and redirect to login
      localStorage.removeItem('token');
      window.location.href = '/login';
    }

    return Promise.reject(error);
  }
);

// Mock data for development if API is not available
const mockBooks = [
  { id: 1, title: 'To Kill a Mockingbird', author: 'Harper Lee', isbn: '9780061120084', publisher: 'HarperCollins', publicationYear: 1960, quantity: 5, availableQuantity: 3 },
  { id: 2, title: '1984', author: 'George Orwell', isbn: '9780451524935', publisher: 'Signet Classic', publicationYear: 1949, quantity: 7, availableQuantity: 4 },
  { id: 3, title: 'The Great Gatsby', author: 'F. Scott Fitzgerald', isbn: '9780743273565', publisher: 'Scribner', publicationYear: 1925, quantity: 4, availableQuantity: 2 }
];

const mockMembers = [
  { id: 1, name: 'John Doe', email: 'john@example.com', phoneNumber: '1234567890', address: '123 Main St', role: 'USER', membershipStartDate: '2023-01-01', membershipEndDate: '2024-01-01' },
  { id: 2, name: 'Jane Smith', email: 'jane@example.com', phoneNumber: '0987654321', address: '456 Oak Ave', role: 'USER', membershipStartDate: '2023-02-15', membershipEndDate: '2024-02-15' }
];

const mockBorrowings = [
  { id: 1, bookId: 1, bookTitle: 'To Kill a Mockingbird', memberId: 1, memberName: 'John Doe', borrowDate: '2023-05-01', dueDate: '2023-05-15', returnDate: null, returned: false, fineAmount: 0 },
  { id: 2, bookId: 2, bookTitle: '1984', memberId: 2, memberName: 'Jane Smith', borrowDate: '2023-04-15', dueDate: '2023-04-29', returnDate: '2023-04-28', returned: true, fineAmount: 0 }
];

// Helper function to use mock data if API fails
const withMockFallback = (apiCall, mockData) => {
  return async (...args) => {
    try {
      return await apiCall(...args);
    } catch (error) {
      console.warn('Using mock data due to API error:', error.message);
      return { data: mockData };
    }
  };
};

// Auth API
export const authAPI = {
  login: (credentials) => api.post('/auth/login', credentials),
  register: (userData) => api.post('/auth/signup', userData),
};

// Books API
export const booksAPI = {
  getAll: withMockFallback(() => api.get('/books'), mockBooks),
  getById: withMockFallback((id) => api.get(`/books/${id}`), (id) => mockBooks.find(b => b.id === parseInt(id))),
  create: async (bookData) => {
    try {
      const response = await api.post('/books', bookData);
      return response;
    } catch (error) {
      console.warn('Using mock data for book creation:', error.message);
      const newBook = { ...bookData, id: mockBooks.length + 1, availableQuantity: bookData.quantity };
      mockBooks.push(newBook);
      return { data: newBook };
    }
  },
  update: async (id, bookData) => {
    try {
      const response = await api.put(`/books/${id}`, bookData);
      return response;
    } catch (error) {
      console.warn('Using mock data for book update:', error.message);
      const index = mockBooks.findIndex(b => b.id === parseInt(id));
      if (index !== -1) {
        mockBooks[index] = { ...bookData, id: parseInt(id), availableQuantity: bookData.quantity };
        return { data: mockBooks[index] };
      }
      throw new Error('Book not found');
    }
  },
  delete: async (id) => {
    try {
      const response = await api.delete(`/books/${id}`);
      return response;
    } catch (error) {
      console.warn('Using mock data for book deletion:', error.message);
      const index = mockBooks.findIndex(b => b.id === parseInt(id));
      if (index !== -1) {
        mockBooks.splice(index, 1);
        return { data: { message: 'Book deleted successfully' } };
      }
      throw new Error('Book not found');
    }
  },
};

// Members API
export const membersAPI = {
  getAll: withMockFallback(() => api.get('/members'), mockMembers),
  getById: withMockFallback((id) => api.get(`/members/${id}`), (id) => mockMembers.find(m => m.id === parseInt(id))),
  create: (memberData) => api.post('/members', memberData),
  update: (id, memberData) => api.put(`/members/${id}`, memberData),
  delete: (id) => api.delete(`/members/${id}`),
};

// Borrowings API
export const borrowingsAPI = {
  getAll: withMockFallback(() => api.get('/borrowings'), mockBorrowings),
  getById: withMockFallback((id) => api.get(`/borrowings/${id}`), (id) => mockBorrowings.find(b => b.id === parseInt(id))),
  create: (borrowingData) => {
    try {
      return api.post('/borrowings', borrowingData);
    } catch (error) {
      console.warn('Using mock data for borrowing creation');
      const book = mockBooks.find(b => b.id === parseInt(borrowingData.bookId));
      const member = mockMembers.find(m => m.id === parseInt(borrowingData.memberId));

      if (book && member) {
        const newBorrowing = {
          id: mockBorrowings.length + 1,
          bookId: book.id,
          bookTitle: book.title,
          memberId: member.id,
          memberName: member.name,
          borrowDate: borrowingData.borrowDate || new Date().toISOString().split('T')[0],
          dueDate: borrowingData.dueDate,
          returnDate: null,
          returned: false,
          fineAmount: 0
        };

        mockBorrowings.push(newBorrowing);
        book.availableQuantity -= 1;
        return { data: newBorrowing };
      }

      throw new Error('Book or member not found');
    }
  },
  returnBook: (id) => {
    try {
      return api.put(`/borrowings/${id}/return`);
    } catch (error) {
      console.warn('Using mock data for returning book');
      const borrowing = mockBorrowings.find(b => b.id === parseInt(id));

      if (borrowing && !borrowing.returned) {
        borrowing.returned = true;
        borrowing.returnDate = new Date().toISOString().split('T')[0];

        const book = mockBooks.find(b => b.id === borrowing.bookId);
        if (book) {
          book.availableQuantity += 1;
        }

        return { data: borrowing };
      }

      throw new Error('Borrowing not found or already returned');
    }
  },
};

export default api;
