import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Check if user is already logged in
    const token = localStorage.getItem('token');
    if (token) {
      // Set the default Authorization header for all requests
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // Get user info if token exists
      fetchCurrentUser();
    } else {
      setLoading(false);
    }
  }, []);

  const fetchCurrentUser = async () => {
    try {
      // Get user info from the backend
      const response = await axios.get('/api/auth/me');
      console.log('Current user data from API:', response.data);
      setCurrentUser(response.data);
    } catch (err) {
      console.error('Error fetching current user:', err);
      setError('Failed to fetch user information');
      // Clear token if it's invalid
      localStorage.removeItem('token');
      delete axios.defaults.headers.common['Authorization'];
    } finally {
      setLoading(false);
    }
  };

  const login = async (email, password) => {
    try {
      setLoading(true);
      setError(null);

      const response = await axios.post('/api/auth/login', { email, password });
      const { accessToken } = response.data;

      // Save token to localStorage
      localStorage.setItem('token', accessToken);

      // Set the default Authorization header for all requests
      axios.defaults.headers.common['Authorization'] = `Bearer ${accessToken}`;

      // Fetch current user info
      await fetchCurrentUser();

      return true;
    } catch (err) {
      console.error('Login error:', err);
      setError(err.response?.data?.error || 'Failed to login');
      return false;
    } finally {
      setLoading(false);
    }
  };

  const register = async (userData) => {
    try {
      setLoading(true);
      setError(null);

      const response = await axios.post('/api/auth/signup', userData);

      // After successful registration, redirect to login page
      // The user will need to log in with their credentials
      return true;
    } catch (err) {
      console.error('Registration error:', err);
      setError(err.response?.data?.error || 'Failed to register');
      return false;
    } finally {
      setLoading(false);
    }
  };

  const logout = () => {
    // Remove token from localStorage
    localStorage.removeItem('token');

    // Remove Authorization header
    delete axios.defaults.headers.common['Authorization'];

    // Clear user state
    setCurrentUser(null);
  };

  const isAdmin = () => {
    console.log('isAdmin check - currentUser:', currentUser);
    const result = currentUser && currentUser.role === 'ADMIN';
    console.log('isAdmin result:', result);
    return result;
  };

  const value = {
    currentUser,
    loading,
    error,
    login,
    register,
    logout,
    isAdmin
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
