import axios from 'axios';

// Create axios instance with base URL
const api = axios.create({
  baseURL: 'http://localhost:8080/api',
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add a request interceptor to add the auth token to requests
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers['Authorization'] = `Bearer ${token}`;
    }
    console.log('API Request:', config.method.toUpperCase(), config.url, config.data);
    return config;
  },
  (error) => {
    console.error('API Request Error:', error);
    return Promise.reject(error);
  }
);

// Add a response interceptor to handle errors
api.interceptors.response.use(
  (response) => {
    console.log('API Response:', response.status, response.data);
    return response;
  },
  (error) => {
    console.error('API Response Error:', error.response?.status, error.response?.data || error.message);

    // Handle 401 Unauthorized errors
    if (error.response && error.response.status === 401) {
      // Clear token and redirect to login
      localStorage.removeItem('token');
      window.location.href = '/login';
    }

    return Promise.reject(error);
  }
);

// Auth API
export const authAPI = {
  login: (credentials) => api.post('/auth/login', credentials),
  register: (userData) => api.post('/auth/signup', userData),
  getCurrentUser: () => api.get('/auth/me'),
};

// Books API
export const booksAPI = {
  getAll: () => api.get('/books'),
  getById: (id) => api.get(`/books/${id}`),
  create: (bookData) => api.post('/books', bookData),
  update: (id, bookData) => api.put(`/books/${id}`, bookData),
  delete: (id) => api.delete(`/books/${id}`),
};

// Members API
export const membersAPI = {
  getAll: () => api.get('/members'),
  getById: (id) => api.get(`/members/${id}`),
  create: (memberData) => api.post('/members', memberData),
  update: (id, memberData) => api.put(`/members/${id}`, memberData),
  delete: (id) => api.delete(`/members/${id}`),
};

// Borrowings API
export const borrowingsAPI = {
  getAll: () => api.get('/borrowings'),
  getById: (id) => api.get(`/borrowings/${id}`),
  create: (borrowingData) => api.post('/borrowings', borrowingData),
  update: (id, borrowingData) => api.put(`/borrowings/${id}`, borrowingData),
  returnBook: (id, returnData) => api.put(`/borrowings/${id}/return`, returnData),
  delete: (id) => api.delete(`/borrowings/${id}`),
  getByMember: (memberId) => api.get(`/borrowings/member/${memberId}`),
  getByBook: (bookId) => api.get(`/borrowings/book/${bookId}`),
  getActive: () => api.get('/borrowings/active'),
  getOverdue: () => api.get('/borrowings/overdue'),
};

// Stats API
export const statsAPI = {
  getDashboardStats: () => api.get('/stats/dashboard'),
  getBookStats: () => api.get('/stats/books'),
  getMemberStats: () => api.get('/stats/members'),
  getBorrowingStats: () => api.get('/stats/borrowings'),
};

export default api;
