import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Typography,
  Box,
  Button,
  TextField,
  Grid,
  Card,
  CardContent,
  CardActions,
  CardMedia,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  CircularProgress,
  Alert,
  Snackbar,
  InputAdornment
} from '@mui/material';
import {
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  Search as SearchIcon,
  Clear as ClearIcon
} from '@mui/icons-material';
import { booksAPI } from '../services/api';

// Book form component for add/edit
const BookForm = ({ book, onSubmit, onCancel }) => {
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    isbn: '',
    publisher: '',
    publicationYear: '',
    quantity: 1,
    ...book
  });
  const [formErrors, setFormErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: name === 'publicationYear' || name === 'quantity' ? 
        (value === '' ? '' : parseInt(value, 10)) : value
    });

    // Clear error when user types
    if (formErrors[name]) {
      setFormErrors({
        ...formErrors,
        [name]: ''
      });
    }
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.title) {
      errors.title = 'Title is required';
    }

    if (!formData.author) {
      errors.author = 'Author is required';
    }

    if (!formData.isbn) {
      errors.isbn = 'ISBN is required';
    }

    if (!formData.publisher) {
      errors.publisher = 'Publisher is required';
    }

    if (!formData.publicationYear) {
      errors.publicationYear = 'Publication year is required';
    } else if (formData.publicationYear < 1000 || formData.publicationYear > new Date().getFullYear()) {
      errors.publicationYear = 'Invalid publication year';
    }

    if (!formData.quantity) {
      errors.quantity = 'Quantity is required';
    } else if (formData.quantity < 1) {
      errors.quantity = 'Quantity must be at least 1';
    }

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      onSubmit(formData);
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} noValidate>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <TextField
            required
            fullWidth
            id="title"
            label="Title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            error={!!formErrors.title}
            helperText={formErrors.title}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            required
            fullWidth
            id="author"
            label="Author"
            name="author"
            value={formData.author}
            onChange={handleChange}
            error={!!formErrors.author}
            helperText={formErrors.author}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            fullWidth
            id="isbn"
            label="ISBN"
            name="isbn"
            value={formData.isbn}
            onChange={handleChange}
            error={!!formErrors.isbn}
            helperText={formErrors.isbn}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            fullWidth
            id="publisher"
            label="Publisher"
            name="publisher"
            value={formData.publisher}
            onChange={handleChange}
            error={!!formErrors.publisher}
            helperText={formErrors.publisher}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            fullWidth
            id="publicationYear"
            label="Publication Year"
            name="publicationYear"
            type="number"
            value={formData.publicationYear}
            onChange={handleChange}
            error={!!formErrors.publicationYear}
            helperText={formErrors.publicationYear}
            inputProps={{ min: 1000, max: new Date().getFullYear() }}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            fullWidth
            id="quantity"
            label="Quantity"
            name="quantity"
            type="number"
            value={formData.quantity}
            onChange={handleChange}
            error={!!formErrors.quantity}
            helperText={formErrors.quantity}
            inputProps={{ min: 1 }}
          />
        </Grid>
      </Grid>
      <Box sx={{ mt: 3, display: 'flex', justifyContent: 'flex-end' }}>
        <Button onClick={onCancel} sx={{ mr: 1 }}>
          Cancel
        </Button>
        <Button type="submit" variant="contained">
          {book ? 'Update' : 'Add'} Book
        </Button>
      </Box>
    </Box>
  );
};

// Book card component
const BookCard = ({ book, onEdit, onDelete }) => {
  const navigate = useNavigate();

  const handleClick = () => {
    navigate(`/books/${book.id}`);
  };

  return (
    <Card 
      sx={{ 
        height: '100%', 
        display: 'flex', 
        flexDirection: 'column',
        transition: 'transform 0.2s',
        '&:hover': {
          transform: 'translateY(-5px)',
          boxShadow: '0 8px 16px rgba(0,0,0,0.1)'
        }
      }}
    >
      <CardMedia
        component="div"
        sx={{
          height: 140,
          backgroundColor: 'primary.light',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          color: 'white',
          fontWeight: 'bold',
          fontSize: '1.2rem'
        }}
      >
        {book.title}
      </CardMedia>
      <CardContent sx={{ flexGrow: 1 }}>
        <Typography gutterBottom variant="h6" component="div">
          {book.title}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          By {book.author}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          ISBN: {book.isbn}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Publisher: {book.publisher}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Year: {book.publicationYear}
        </Typography>
        <Typography variant="body2" color="text.primary" sx={{ mt: 1 }}>
          Available: {book.availableQuantity} / {book.quantity}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small" onClick={handleClick}>
          View Details
        </Button>
        <Box sx={{ flexGrow: 1 }} />
        <IconButton size="small" color="primary" onClick={() => onEdit(book)}>
          <EditIcon />
        </IconButton>
        <IconButton size="small" color="error" onClick={() => onDelete(book)}>
          <DeleteIcon />
        </IconButton>
      </CardActions>
    </Card>
  );
};

const Books = () => {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  // Dialog states
  const [openAddDialog, setOpenAddDialog] = useState(false);
  const [openEditDialog, setOpenEditDialog] = useState(false);
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const [currentBook, setCurrentBook] = useState(null);

  // Snackbar state
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'success'
  });

  const fetchBooks = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('Fetching books...');

      const response = await booksAPI.getAll();
      console.log('Books fetched:', response.data);

      if (Array.isArray(response.data)) {
        setBooks(response.data);
        setFilteredBooks(response.data);
      } else {
        console.warn('Unexpected response format:', response.data);
        setBooks([]);
        setFilteredBooks([]);
        setError('Received invalid data format from server');
      }
    } catch (err) {
      console.error('Error fetching books:', err);
      let errorMessage = 'Failed to load books. Please try again later.';

      if (err.response) {
        errorMessage = `Server error (${err.response.status}): ${err.response.data?.message || err.response.data?.error || 'Unknown error'}`;
      } else if (err.request) {
        errorMessage = 'No response from server. Please check your connection.';
      } else {
        errorMessage = err.message || errorMessage;
      }

      setError(errorMessage);
      // If we have no books yet, set empty arrays to avoid undefined errors
      if (!books.length) {
        setBooks([]);
        setFilteredBooks([]);
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  useEffect(() => {
    if (searchTerm) {
      const filtered = books.filter(book => 
        book.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        book.author.toLowerCase().includes(searchTerm.toLowerCase()) ||
        book.isbn.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredBooks(filtered);
    } else {
      setFilteredBooks(books);
    }
  }, [searchTerm, books]);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const clearSearch = () => {
    setSearchTerm('');
  };

  // Add book
  const handleAddBook = async (bookData) => {
    try {
      setLoading(true);
      console.log('Adding book:', bookData);

      const response = await booksAPI.create(bookData);
      console.log('Book added response:', response);

      setSnackbar({
        open: true,
        message: 'Book added successfully',
        severity: 'success'
      });

      setOpenAddDialog(false);
      fetchBooks();
    } catch (err) {
      console.error('Error adding book:', err);
      let errorMessage = 'Failed to add book';

      if (err.response) {
        errorMessage = err.response.data?.message || err.response.data?.error || `Server error: ${err.response.status}`;
      } else if (err.request) {
        errorMessage = 'No response from server. Please check your connection.';
      } else {
        errorMessage = err.message || errorMessage;
      }

      setSnackbar({
        open: true,
        message: errorMessage,
        severity: 'error'
      });
    } finally {
      setLoading(false);
    }
  };

  // Edit book
  const handleEditBook = async (bookData) => {
    try {
      setLoading(true);
      console.log('Updating book:', bookData);

      const response = await booksAPI.update(currentBook.id, bookData);
      console.log('Book updated response:', response);

      setSnackbar({
        open: true,
        message: 'Book updated successfully',
        severity: 'success'
      });

      setOpenEditDialog(false);
      fetchBooks();
    } catch (err) {
      console.error('Error updating book:', err);
      let errorMessage = 'Failed to update book';

      if (err.response) {
        errorMessage = err.response.data?.message || err.response.data?.error || `Server error: ${err.response.status}`;
      } else if (err.request) {
        errorMessage = 'No response from server. Please check your connection.';
      } else {
        errorMessage = err.message || errorMessage;
      }

      setSnackbar({
        open: true,
        message: errorMessage,
        severity: 'error'
      });
    } finally {
      setLoading(false);
    }
  };

  // Delete book
  const handleDeleteBook = async () => {
    try {
      setLoading(true);
      console.log('Deleting book:', currentBook.id);

      const response = await booksAPI.delete(currentBook.id);
      console.log('Book deleted response:', response);

      setSnackbar({
        open: true,
        message: 'Book deleted successfully',
        severity: 'success'
      });

      setOpenDeleteDialog(false);
      fetchBooks();
    } catch (err) {
      console.error('Error deleting book:', err);
      let errorMessage = 'Failed to delete book';

      if (err.response) {
        errorMessage = err.response.data?.message || err.response.data?.error || `Server error: ${err.response.status}`;
      } else if (err.request) {
        errorMessage = 'No response from server. Please check your connection.';
      } else {
        errorMessage = err.message || errorMessage;
      }

      setSnackbar({
        open: true,
        message: errorMessage,
        severity: 'error'
      });
    } finally {
      setLoading(false);
    }
  };

  const handleCloseSnackbar = () => {
    setSnackbar({
      ...snackbar,
      open: false
    });
  };

  if (loading && books.length === 0) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" component="h1">
          Books
        </Typography>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={() => setOpenAddDialog(true)}
        >
          Add Book
        </Button>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      <Box sx={{ mb: 3 }}>
        <TextField
          fullWidth
          placeholder="Search by title, author, or ISBN"
          value={searchTerm}
          onChange={handleSearchChange}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <SearchIcon />
              </InputAdornment>
            ),
            endAdornment: searchTerm && (
              <InputAdornment position="end">
                <IconButton size="small" onClick={clearSearch}>
                  <ClearIcon />
                </IconButton>
              </InputAdornment>
            )
          }}
        />
      </Box>

      {filteredBooks.length === 0 ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '200px' }}>
          <Typography variant="body1" color="text.secondary">
            {searchTerm ? 'No books match your search' : 'No books available'}
          </Typography>
        </Box>
      ) : (
        <Grid container spacing={3}>
          {filteredBooks.map(book => (
            <Grid item key={book.id} xs={12} sm={6} md={4} lg={3}>
              <BookCard 
                book={book} 
                onEdit={(book) => {
                  setCurrentBook(book);
                  setOpenEditDialog(true);
                }}
                onDelete={(book) => {
                  setCurrentBook(book);
                  setOpenDeleteDialog(true);
                }}
              />
            </Grid>
          ))}
        </Grid>
      )}

      {/* Add Book Dialog */}
      <Dialog open={openAddDialog} onClose={() => setOpenAddDialog(false)} maxWidth="sm" fullWidth>
        <DialogTitle>Add New Book</DialogTitle>
        <DialogContent>
          <BookForm 
            onSubmit={handleAddBook} 
            onCancel={() => setOpenAddDialog(false)} 
          />
        </DialogContent>
      </Dialog>

      {/* Edit Book Dialog */}
      <Dialog open={openEditDialog} onClose={() => setOpenEditDialog(false)} maxWidth="sm" fullWidth>
        <DialogTitle>Edit Book</DialogTitle>
        <DialogContent>
          {currentBook && (
            <BookForm 
              book={currentBook} 
              onSubmit={handleEditBook} 
              onCancel={() => setOpenEditDialog(false)} 
            />
          )}
        </DialogContent>
      </Dialog>

      {/* Delete Book Dialog */}
      <Dialog open={openDeleteDialog} onClose={() => setOpenDeleteDialog(false)}>
        <DialogTitle>Delete Book</DialogTitle>
        <DialogContent>
          <Typography>
            Are you sure you want to delete "{currentBook?.title}"?
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDeleteDialog(false)}>Cancel</Button>
          <Button onClick={handleDeleteBook} color="error">Delete</Button>
        </DialogActions>
      </Dialog>

      {/* Snackbar for notifications */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
      >
        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default Books;
